import React, { Component } from 'react';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

// //handleClick function


// create a component
class ContactUs extends React.Component {
    state = {
        name: '', // or what you want
        email: '', // or what you want
        dec: '', // or what you want
        send: false,
    }

    //handleChange function
 handleChange = (event) => {
    const {name, value} = event.target
    this.setState({
        [name]: value
    })
}

 handleClick  () {
    this.setState({ send: true })
}



    render() {  
        return (
            <React.Fragment>
                <div>
                    <h2>Contact Us</h2>
                    <hr className="bg-warning pb-1" />
                </div>

                <Container>
                    <Form>
                        <Form.Group controlId="form.Name">
                            <Form.Label>Name</Form.Label>
                            <Form.Control type="text" placeholder="Enter name" />
                        </Form.Group>
                        <Form.Group controlId="form.Email">
                            <Form.Label>Email address</Form.Label>
                            <Form.Control type="email" placeholder="name@example.com" />
                        </Form.Group>
                        <Form.Group controlId="form.Textarea">
                            <Form.Label>Comment</Form.Label>
                            <Form.Control as="textarea" rows={3} />
                        </Form.Group>

                        <Button onClick={handleClick} className="mt-5">Send</Button>
                        {this.state.send &&
                            <Credentials
                                name={this.state.name}
                                email={this.state.email}
                                des={this.state.des}
                            />
                        }
                    </Form>
                </Container>

            </React.Fragment>
        );
    }
}

const Credentials = ({ name, email, des }) => {
    return (
        <div>
            <h2>Name is: {name}</h2>
            <h4>Email is: {email}</h4>
            <p>Description is: {des}</p>
        </div>
    )
}


export default ContactUs;
