import React, { Component } from 'react';

// create a component
class Listing extends React.Component {
    state = {
      index : 1,
      users : []
    }
    async componentDidMount(){
      const url = "https://reqres.in/api/users";
      const response = await fetch(url);
      const data = await response.json();
      this.setState({users: data.data});
    }
    render() {
        return (
            <React.Fragment>
                <div>
                    <h2>Listing Page</h2>
                    <hr className="bg-warning pb-1" />
                </div>
                <table className="table table-striped table-hovered table-bordered">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Name</th>
                            <th>Email</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.users.map((data,index) => (
                        <tr>
                            <td key={index.toString()}>{data.id}</td>
                            <td key={index.toString()}>{data.first_name} {data.last_name}</td>
                            <td key={index.toString()}>{data.email}</td>
                        </tr>
                        ))}
                    </tbody>
                </table>
            </React.Fragment>
        );
    }
}

export default Listing;
