import React, { Component } from 'react';
import Order from './rder.jsx';

// create a component
class Listing extends React.Component {
    state = {
      index : 1,
      products : []
    }
    async componentDidMount(){
      const url = "https://fakestoreapi.com/products";
      const response = await fetch(url);
      const data = await response.json();
      console.log(data)
      this.setState({products: data});
    }
  

    render() {
        return (
            <React.Fragment>
                <div className="container mt-3">
                    <div>
                        <h2>Products Listing</h2>
                        <hr className="bg-warning pb-1" />
                    </div>
                    <div className="row mb-5">
                        {this.state.products.map((data,index) => (
                        <div className="col-md-3 mt-3">
                            <div className="card">
                                <div className="card-header text-white bg-secondary text-center">
                                    <p key={index.toString()}><b>Category: </b>{data.category}</p>
                                </div>
                                <div className="card-body text-center">
                                    <img className='mb-3' src={data.image} width="100hv" alt="" />
                                </div>
                                <div className="card-footer">
                                    <h6><b>Name: </b>{data.title}</h6>
                                    <p><b>Price: </b>${data.price}</p>
                                    <button className='btn btn-warning text-white' ><b>ORDER</b></button>
                                </div>
                            </div>
                        </div>
                        ))}
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default Listing;
