import React from "react";
import "./styles.css";
import Home from "./../../components/Pages/Home";
import Login from "./../../components/Pages/Login";
import Register from "./../../components/Pages/Register";
import Listing from "./../../components/Pages/Listing";
import ContactUs from "./../../components/Pages/ContactUs";


class ClassHeader extends React.Component{
    state = {
        key : 0,
        menuName : "",
        status : false,
    }
    handleClick = (i,header) =>{
        this.setState({menuName: header});
        this.setState({status: true});
        this.setState({key: i});
    }
    renderClassName = ()=> {
        if (this.state.status == true) {
            switch (this.state.menuName) {
                case "Login":
                    return <Login/>
                case "Register":
                    return <Register/>
                case "Listing":
                    return <Listing/>
                // case "Detail":
                //     return <Detail/>
                case "Contact Us":
                    return <ContactUs/>
                case "Logo":
                    return <Home/>
                default:
                    return <Home/>
            }
        } else {
            return <Home/>
        }
    }
    render(){
        const {headers =[],headersRight} = this.props;
        return (
            <React.Fragment>
                <nav className="navbar navbar-expand-lg  bg-dark navbar-dark sticky-top">
                    <a className="navbar-brand" href="#" onClick={()=> this.handleClick(0,"Logo") }>
                        <img src="./logo192.png" alt=""/>
                    </a>
                    <div className="collapse navbar-collapse">
                        <ul className="navbar-nav mr-auto justify-content-center text-center mx-md-auto mx-0">
                            {headers.map((header,key) => {
                                return <li key={key.toString()} className="nav-item">
                                    <a className="nav-link" onClick={()=> this.handleClick(key,header) } defaultValue={header}>{header}</a></li>
                            })}
                        </ul>
                        <ul className="navbar-nav navbar-right">
                            {headersRight.map((menuRight,key) => {
                                return <li key={key.toString()} className="nav-item">
                                    <a className="nav-link" onClick={()=> this.handleClick(key,menuRight) }>{menuRight}</a></li>
                            })}
                        </ul>
                    </div>
                </nav>

                <div className="container ">
                    {this.renderClassName()}
                </div>

                <footer className="page-footer bg-dark text-white">
                    <div className="footer-copyright  text-center py-3">© 2022 Copyright:
                        <span><b> Group 4</b></span>
                    </div>
                </footer>

            </React.Fragment>
        );
    }
}
export default ClassHeader;
