import React, { Component } from 'react';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

// //handleClick function

// create a component
class ContactUs extends React.Component {
    state = {
        name: '',
        email: '',
        des: '',
        send: false,
    }

    //handleChange function
    handleChange = (event) => {
        const { name, value } = event.target
        this.setState({
            [name]: value
        })
    }

    handleClick = () => {
        this.setState({ send: true })
    }

    render() {
        return (
            <React.Fragment>
                <div className='container mt-3'>
                    <div>
                        <h2>Contact Us</h2>
                        <hr className="bg-warning pb-1" />
                    </div>
                </div>
                <div className="container">
                    <div className="row">
                        <div className="col-6">
                            <div className="card">
                                <div className="card-header">
                                    Fill In Your Information
                                </div>
                                <div className="card-body">
                                    <form action="#">
                                        <div className="form-group">
                                            <div className="row">
                                                <div className="col">
                                                    <label htmlFor="Name">Name</label>
                                                    <input type="text" placeholder='Name' onChange={this.handleChange} name='name' required className='form-control' />
                                                </div>
                                                <div className="col">
                                                    <label htmlFor="Email">Email</label>
                                                    <input type="email" required name='email' onChange={this.handleChange} placeholder='Email' className='form-control' />
                                                </div>
                                            </div>
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="Description">Description</label>
                                            <textarea name="des" id="" cols="30" onChange={this.handleChange} placeholder='Description' rows="3" className='form-control'></textarea>
                                        </div>
                                    </form>
                                    <button type='button' onClick={this.handleClick} className="btn btn-primary mt-3 float-right" name='send' >Send</button>
                                    <button type="reset" id='reset' onClick={this.Reset} className="btn btn-danger mt-3 " name='reset' >Reset</button>
                                   
                                </div>
                            </div>
                        </div>
                        <div className="col-6">
                        {this.state.send &&
                                        <Credentials
                                            name={this.state.name}
                                            email={this.state.email}
                                            des={this.state.des}
                                        />
                                    }
                        </div>
                    </div>
                </div>


            </React.Fragment>
        );
    }
}

const Credentials = ({ name, email, des }) => {
    return (
        <div className='card'>
            <div className='card-header'>
                <h2>Your Information</h2>
            </div>
            <div className='card-body'>
                <p><b>Name is:</b> {name}</p>
                <p><b>Email is:</b> {email}</p>
                <p><b>Description is:</b> {des}</p>
            </div>
        </div>
    )

}




export default ContactUs;
