// import code ....
state = {
    firstName: '', // or what you want
    lastName: '', // or what you want
    email: '', // or what you want
    send: false,
 }
 
 //handleChange function
 const handleChange = (event) => {
     const {name, value} = event.target
     this.setState({
         [name]: value
     })
 }
 
 //handleClick function
 const handleClick = () => {
     this.setState({send: true})
 }
