import React, { Component } from 'react';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

// //handleClick function


// create a component
class ContactUs extends React.Component {
    state = {
        name: '',
        email: '',
        des: '',
        send: false,
    }

    //handleChange function
    handleChange = (event) => {
        const { name, value } = event.target
        this.setState({
            [name]: value
        })
    }

    handleClick = () => {
        this.setState({ send: true })
    }

    render() {
        return (
            <React.Fragment>
                <div>
                    <h2>Contact Us</h2>
                    <hr className="bg-warning pb-1" />
                </div>

                <Container>
                    <Form>
                        <Form.Group controlId="form.Name">
                            <Form.Label>Name</Form.Label>
                            <Form.Control name="name" type="text" onChange={this.handleChange} placeholder="Enter name" />
                        </Form.Group>
                        <Form.Group controlId="form.Email">
                            <Form.Label>Email address</Form.Label>
                            <Form.Control type="email" name="email" onChange={this.handleChange} placeholder="name@example.com" />
                        </Form.Group>
                        <Form.Group controlId="form.Textarea">
                            <Form.Label>Comment</Form.Label>
                            <Form.Control name="des" onChange={this.handleChange} as="textarea" rows={3} />
                        </Form.Group>

                        <Button name='send' onClick={this.handleClick} className="mt-5">Send</Button>
                        {this.state.send &&
                            <Credentials
                                name={this.state.name}
                                email={this.state.email}
                                des={this.state.des}
                            />
                        }
                    </Form>
                </Container>

            </React.Fragment>
        );
    }
}

const Credentials = ({ name, email, des }) => {
    return (
        <div className='card mt'>
            <p>Name is: {name}</p>
            <p>Email is: {email}</p>
            <p>Description is: {des}</p>
        </div>
    )

}


export default ContactUs;
